class DataBase:
    def __init__(self, host, user, password, db) -> None:
        """
        
        :param host: 
        :param user: 
        :param password: 
        :param db: 
        """
        self.conn = pymysql.connect(
            host=host,
            user=user,
            password=password,
            db=db,
            charset='utf8'
        )
        self.cursor = self.conn.cursor()

    def query(self, sql) -> tuple:
        """
        
        :param sql: sql
        """
        # SQL
        self.cursor.execute(sql)
        # 
        result = self.cursor.fetchall()
        return result

    def execute(self, sql):
        """
        
        :param sql: sql
        """
        try:
            # SQL
            self.cursor.execute(sql)
            # 
            self.conn.commit()
        except Exception as e:
            # 
            print(e)
            if self.conn:
                self.conn.rollback()

    def close(self) -> None:
        """
        
        """
        self.cursor.close()
        self.conn.close()


