class EncodeDecrypt:
    # AES
    @staticmethod
    def aes_encrypt_decrypt(text, key, is_foll=False):
        """
        :param text:
        :param key:
        :param is_foll:False
        """

        def to_16(key):
            """
            16bytes
            :param key:
            :return:
            """
            if len(key) > 32:
                keys = key[:32]
            else:
                keys = key
            keyes = bytes(keys, encoding="utf8")
            while len(keyes) % 16 != 0:
                keyes += b'\0'
            return keyes  # bytes

        aes = AES.new(to_16(key), AES.MODE_ECB)  # 
        if is_foll is False:
            return str(base64.encodebytes(aes.encrypt(to_16(text))), encoding='utf8').replace('\n', '')  # 
        return str(aes.decrypt(base64.decodebytes(bytes(text, encoding='utf8'))).rstrip(b'\0').decode("utf8"))  # 

    # RSA 
    @staticmethod
    def rsa_encrypt_decrypt(message, pubkey=None, private_key=None, number=2048):
        """
        :param message:
        :param pubkey: 
        :param private_key: 
        :param number: 
        """
        if pubkey is None or private_key is None:
            pubkey, private_key = rsa.newkeys(number)

        if isinstance(message, str):
            content = message.encode('utf-8')
            crypto = rsa.encrypt(content, pubkey)
            return crypto, pubkey, private_key
        else:
            try:
                content = rsa.decrypt(message, private_key)
                con = content.decode('utf-8')
                return con
            except rsa.DecryptionError as e:
                print(f": {e}")
                return None

