    # DES3
    @staticmethod
    def des3_encrypt_decrypt(data, key=None, is_fell=False):
        def generate_des3_key():
            while True:
                key = token_bytes(24)
                if DES3.adjust_key_parity(key):
                    return key
        if key is None:
            key = generate_des3_key()
        else:
            key = key
        mode = DES3.MODE_ECB
        if is_fell is False:
            if not isinstance(data, bytes):
                data = bytes(data, 'utf-8')
            x = len(data) % 8
            data = data + b'\0' * x
            cryptor = DES3.new(key, mode)
